import { useState } from 'react';

const ItemCount = () => {
    const [numero, setNumero] = useState(0)

    const sumar = () => setNumero (numero+1)

    return (
        <>
            <button className="btn btn-dark" onClick={()=> sumar()}>+</button>
            {numero}
        </>
    );
}

export default ItemCount;
