import { useState } from 'react';

const ItemCount = () => {
    const [numero, setNumero] = useState(1);

   const sumar = () => {
    if(numero < 10)
    setNumero(numero+1)
   }

   const restar = () => {
    if(numero > 1)
    setNumero(numero-1)
   }

    return (
        <>
            <button className="btn btn-dark" onClick={ () => sumar()}>+</button>
            {numero}
            <button className='btn btn-light' onClick={ () => restar()}> - </button>
        </>
    );
}

export default ItemCount;
